    private void Z(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Z

        switch (evt.getActionCommand()) {

            case "0":

                fe.setText(fe.getText() + "0");

                break;

            case "1":
                if (fe.getText().length() > 0) {
                    fe.setText(fe.getText() + "1");
                }
                break;

            case "2":
                fe.setText(fe.getText() + "2");
                break;

            case "3":
                fe.setText(fe.getText() + "3");
                break;

            case "4":
                fe.setText(fe.getText() + "4");
                break;

            case "5":
                fe.setText(fe.getText() + "5");
                break;

            case "6":
                fe.setText(fe.getText() + "6");
                break;

            case "7":
                fe.setText(fe.getText() + "7");
                break;

            case "8":
                fe.setText(fe.getText() + "8");
                break;

            case "9":
                fe.setText(fe.getText() + "9");
                break;

        }


    }//GEN-LAST:event_Z

