    private void op(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_op

        if (fe.getText().length() > 0) {
                               num1 = Double.parseDouble(fe.getText());
                    fe.setText("");

            switch (evt.getActionCommand()) {
                case "+":
                    op = '+';
                    le.setText(num1 + "+");
                    break;

                        case "-":
                            op = '-';
                            le.setText(num1 + "-");
                            break;
                                case "*":
                                    op = '*';
                                    le.setText(num1 + "*");
                                    break;
                                        case "/":
                                            op = '/';
                                            le.setText(num1 + "/");
                                            break;

                                    
                            }

                        // TODO add your handling code here:
    }//GEN-LAST:event_op
    }
 
